import UIKit

class StylistAvailableWorker
{
    
    let networkLayer = NetworkLayerAlamofire()
    var presenter: StylistAvailablePresentationLogic?
    
    func postRequestGetAvailableTechnicianList(request: StylistAvailable.GetAvailableList.Request) {
        // *********** NETWORK CONNECTION
        
        let errorHandler: (String) -> Void = { (error) in
            self.presenter?.presentError(responseError: error)
        }
        let successHandler: (StylistAvailable.GetAvailableList.Response) -> Void = { (response) in
            self.presenter?.presentSuccess(response: response)
        }
        
        self.networkLayer.post(urlString: ConstantAPINames.getTechnicians.rawValue, body: request, headers: ["X-Request-From": "tma"], successHandler: successHandler, errorHandler: errorHandler, method: .post)
    }
}
