import UIKit

enum SelectStylistAndTimeslot
{
    // MARK: Use cases
    
    enum ServicesWithTimeslot {

        struct Request: Codable {
            let employee_code: String?
            let services: [RequestData]?
        }
        
        struct RequestData: Codable {
            
            var is_dependant: Int?
            var dependant_id: Int?
            
            let service_id: String?
            let start_time: String?
            let service_duration: String?
        }
        
        struct Response: Codable {
            let status: Bool?
            let data: ResponseData?
            let message: String?
        }
        
        struct ResponseData: Codable {
            let services: [ResponseModel]?
        }
        
        struct ResponseModel: Codable {
            let dependant_id: Int?
            let is_dependant: Int?
            let service_id: String?
            let service_duration: String?
            let start_time: String?
            let end_time: String?
        }
    }
}
