import UIKit

class SelectStylistAndTimeslotWorker
{
    
    let networkLayer = NetworkLayerAlamofire()
    var presenter: SelectStylistAndTimeslotPresentationLogic?
    
    func postRequestGetServiceListWithTimeslot(request: SelectStylistAndTimeslot.ServicesWithTimeslot.Request) {
        // *********** NETWORK CONNECTION
        
        let errorHandler: (String) -> Void = { (error) in
            self.presenter?.presentError(responseError: error)
        }
        let successHandler: (SelectStylistAndTimeslot.ServicesWithTimeslot.Response) -> Void = { (response) in
            self.presenter?.presentSuccess(response: response)
        }
        
        self.networkLayer.post(urlString: ConstantAPINames.getServiceListWithTimeslot.rawValue, body: request, headers: ["X-Request-From": "tma"], successHandler: successHandler, errorHandler: errorHandler, method: .post)
    }
    
