        let errorHandler: (String) -> Void = { (error) in
            print(error)
            self.presenter?.presentError(responseError: error)
        }
        let successHandler: (ChangeTimeSlot.AddBlank.Response) -> Void = { (employees) in

            self.presenter?.presentSuccess(response: employees)
        }

        let strUrl = ConstantAPINames.addBlankAppointment.rawValue
        self.networkLayer.post(urlString: strUrl, body: request,
                               headers: ["Content-Type": "application/json",
                                         "X-Request-From": "tma",
                                         "Authorization": "Bearer \(GenericClass.sharedInstance.isuserLoggedIn().accessToken)"],
