import UIKit

protocol BookAppointmentBusinessLogic {
    func doPostCheckValidAddress(request: BookAppointment.CheckValidAddress.Request)
}

class BookAppointmentInteractor: BookAppointmentBusinessLogic {
    var presenter: BookAppointmentPresentationLogic?
    var worker: BookAppointmentWorker?
    //var name: String = ""

    // MARK: Do something

    func doPostCheckValidAddress(request: BookAppointment.CheckValidAddress.Request) {
        worker = BookAppointmentWorker()
        worker?.presenter = presenter
        worker?.postRequestCheckValidAddress(request: request)
    }

}
