import UIKit

class BookAppointmentWorker {
    let networkLayer = NetworkLayerAlamofire()
    var presenter: BookAppointmentPresentationLogic?

    func postRequestCheckValidAddress(request: BookAppointment.CheckValidAddress.Request) {

        let errorHandler: (String) -> Void = { (error) in
            print(error)
            self.presenter?.presentError(responseError: error)
        }
        let successHandler: (BookAppointment.CheckValidAddress.Response) -> Void = { (employees) in

            self.presenter?.presentSuccess(response: employees)
        }

        let strUrl = ConstantAPINames.checkValidAddress.rawValue
        self.networkLayer.post(urlString: strUrl, body: request, headers: ["Content-Type": "application/json", "X-Request-From": "tma"],
                               successHandler: successHandler,
                               errorHandler: errorHandler, method: .post)
    }
}
