import UIKit

class PackageListingWorker {
    let networkLayer = NetworkLayerAlamofire()
    var presenter: PackageListingPresentationLogic?

    func getOfferValuePackages() {

        // Error handling redirection
        let errorHandler: (String) -> Void = { (error) in
            print(error)
            self.presenter?.presentError(responseError: error)
        }

        // Success handling redirection
        let successHandler: (PackageListing.OffersValuePackages.Response) -> Void = { (employees) in
            print(employees)
            self.presenter?.presentSuccess(response: employees)
        }

        // URL <baseurl>/products/?<appendFilters>
        let strURL: String = ConstantAPINames.offersValuePackages.rawValue
       // strURL = strURL.addingPercentEncoding(withAllowedCharacters: CharacterSet.urlQueryAllowed)!
        // GET : API call
        self.networkLayer.get(urlString: strURL, successHandler: successHandler, errorHandler: errorHandler)
    }
}
