import UIKit

protocol DeductionsBusinessLogic
{
  func doSomething(request: Deductions.Something.Request)
}

protocol DeductionsDataStore
{
  //var name: String { get set }
}

class DeductionsInteractor: DeductionsBusinessLogic, DeductionsDataStore
{
  var presenter: DeductionsPresentationLogic?
  var worker: DeductionsWorker?
  //var name: String = ""
  
  // MARK: Do something
  
  func doSomething(request: Deductions.Something.Request)
  {
    worker = DeductionsWorker()
    worker?.doSomeWork()
    
    let response = Deductions.Something.Response()
    presenter?.presentSomething(response: response)
  }
