import UIKit

protocol AchievementPresentationLogic
{
  func presentSomething(response: Achievement.Something.Response)
}

class AchievementPresenter: AchievementPresentationLogic
{
  weak var viewController: AchievementDisplayLogic?
  
  // MARK: Do something
  
  func presentSomething(response: Achievement.Something.Response)
  {
    let viewModel = Achievement.Something.ViewModel()
    viewController?.displaySomething(viewModel: viewModel)
  }
}
