import UIKit

protocol CTCDetailsDisplayLogic: class
{
    func displaySomething(viewModel: CTCDetails.Something.ViewModel)
}

struct CTCDetailsModel {
    let title: String
    let value: String
}

struct CTCBreakUpModel {
    let title: String
    let monthly: String
    let annually: String
    let applyGradient: Bool
}

class CTCDetailsVC: UIViewController, CTCDetailsDisplayLogic
{
    var interactor: CTCDetailsBusinessLogic?
    
    @IBOutlet weak var tableView: UITableView!
    
    // MARK: Object lifecycle
    
    var ctcDetails = [CTCDetailsModel]()
    var ctcBreakUpDetails = [CTCBreakUpModel]()
    
    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?)
    {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }
    
    required init?(coder aDecoder: NSCoder)
    {
        super.init(coder: aDecoder)
        setup()
    }
    
    // MARK: Setup
    
    private func setup()
    {
        let viewController = self
        let interactor = CTCDetailsInteractor()
        let presenter = CTCDetailsPresenter()
        viewController.interactor = interactor
        interactor.presenter = presenter
        presenter.viewController = viewController
    }
    
    // MARK: View lifecycle
    
    override func viewDidLoad()
    {
        super.viewDidLoad()
        doSomething()
        
        tableView.register(UINib(nibName: CellIdentifier.ctcDetailsCell, bundle: nil), forCellReuseIdentifier: CellIdentifier.ctcDetailsCell)
        tableView.register(UINib(nibName: CellIdentifier.ctcBreakUpCell, bundle: nil), forCellReuseIdentifier: CellIdentifier.ctcBreakUpCell)
        tableView.register(UINib(nibName: CellIdentifier.incentiveCommonHeaderCell, bundle: nil), forCellReuseIdentifier: CellIdentifier.incentiveCommonHeaderCell)
        self.ctcDetails = CTCDetails.ctcDetails
        self.ctcBreakUpDetails = CTCDetails.ctcBreakUp
        tableView.reloadData()
    }
    
    override func viewWillAppear(_ animated: Bool) {
        super.viewWillAppear(animated)
        self.navigationController?.navigationBar.isHidden = false
        self.navigationController?.addCustomBackButton(title: "CTC Details")
    }
    
    // MARK: Do something
    
    //@IBOutlet weak var nameTextField: UITextField!
    
    func doSomething()
    {
        let request = CTCDetails.Something.Request()
        interactor?.doSomething(request: request)
    }
    
    func displaySomething(viewModel: CTCDetails.Something.ViewModel)
    {
        //nameTextField.text = viewModel.name
    }
}

extension CTCDetailsVC: UITableViewDelegate, UITableViewDataSource {
    
    func numberOfSections(in tableView: UITableView) -> Int {
        return 3
    }
    
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        if section == 0 {
            return ctcDetails.count
        }
        else if section == 1 {
            return ctcBreakUpDetails.count
        }
        return 1
    }
    
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        
        if indexPath.section == 0 {
            guard let cell = tableView.dequeueReusableCell(withIdentifier: CellIdentifier.ctcDetailsCell, for: indexPath) as? CTCDetailsCell else {
                return UITableViewCell()
            }
            let data = ctcDetails[indexPath.row]
            cell.configureCell(title: data.title, values: data.value)
            cell.selectionStyle = .none
            return cell
        }
        else {
            guard let cell = tableView.dequeueReusableCell(withIdentifier: CellIdentifier.ctcBreakUpCell, for: indexPath) as? CTCBreakUpCell else {
                return UITableViewCell()
            }
            
            if indexPath.section == 2 {
                cell.configureCell(title: "Total CTC", Monthly: "25000", Annually: "300000", cellType: .final)
            }
            else {
                let data = ctcBreakUpDetails[indexPath.row]
                cell.configureCell(title: data.title, Monthly: data.monthly, Annually: data.annually, cellType: data.applyGradient ? .header : .normal)
                cell.selectionStyle = .none
            }
            
            return cell
        }
        
    }
    
    func tableView(_ tableView: UITableView, heightForRowAt indexPath: IndexPath) -> CGFloat {
        return indexPath.section == 0 ? 30 : indexPath.section == 1 ? 50 : 70
    }
    
    func tableView(_ tableView: UITableView, viewForHeaderInSection section: Int) -> UIView? {
        
        if section != 1 {
            return nil
        }
        
        guard let cell = tableView.dequeueReusableCell(withIdentifier: CellIdentifier.incentiveCommonHeaderCell) as? IncentiveCommonHeaderCell else {
            return UITableViewCell()
        }
        cell.setTitle(title: "Full Break Up", identifier: .myDetails, showViewAll: false)
        cell.selectionStyle = .none
        return cell
    }
    
    func tableView(_ tableView: UITableView, heightForHeaderInSection section: Int) -> CGFloat {
        return section == 1 ? 50 : 0
    }
    
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        print("Selection")
    }
}
