    func createFilters(){
        let technicianDataJSON = UserDefaults.standard.value(Dashboard.GetRevenueDashboard.Response.self, forKey: UserDefauiltsKeys.k_key_RevenueDashboard)
        
//        let gender = technicianDataJSON?.data?.filters?.service_gender
