import UIKit

protocol SalesBusinessLogic
{
  func doSomething(request: Sales.Something.Request)
}

protocol SalesDataStore
{
  //var name: String { get set }
}

class SalesInteractor: SalesBusinessLogic, SalesDataStore
{
  var presenter: SalesPresentationLogic?
  var worker: SalesWorker?
  //var name: String = ""
  
  // MARK: Do something
  
  func doSomething(request: Sales.Something.Request)
  {
    worker = SalesWorker()
    worker?.doSomeWork()
    
    let response = Sales.Something.Response()
    presenter?.presentSomething(response: response)
  }
}
