import UIKit

protocol ShiftCalendarBusinessLogic {
    func doGetRosterData(request: MyProfile.GetRosterDetails.Request, method: HTTPMethod)
}

class ShiftCalendarInteractor: ShiftCalendarBusinessLogic
{
  var presenter: ShiftCalendarPresentationLogic?
  var worker: ShiftCalendarWorker?
  //var name: String = ""
  
  // MARK: Do something
  
    func doGetRosterData(request: MyProfile.GetRosterDetails.Request, method: HTTPMethod) {
        worker = ShiftCalendarWorker()
        worker?.presenter = self.presenter
        worker?.postRequestForRosterDetails(request: request, method: method)
    }
}
