import UIKit

protocol PettyCashListingBusinessLogic {
    func doGetPettyCashHistoryRequest(request: PettyCashListing.GetHistory.Request, method: HTTPMethod)
}

class PettyCashListingInteractor: PettyCashListingBusinessLogic {
    var presenter: PettyCashListingPresentationLogic?
    var worker: PettyCashListingWorker?
    //var name: String = ""

    // MARK: Do something

    func doGetPettyCashHistoryRequest(request: PettyCashListing.GetHistory.Request, method: HTTPMethod) {
        worker = PettyCashListingWorker()
        worker?.presenter = self.presenter
        worker?.getRequestForCheckInStatus(request: request, method: method)
    }
}
