import UIKit

protocol TeleConsultationBusinessLogic {
    func doPostGetConsultationList(request: TeleConsultation.GetConsultationRecords.PendingRecordRequest, method: HTTPMethod)
    func doPostGetCompletedList(request: TeleConsultation.GetConsultationRecords.CompletedRecordRequest, method: HTTPMethod)
    func getStatusList()
    func doPostFeedbackRequest(request: TeleConsultation.SubmitFeedback.Request, method: HTTPMethod)
    func doOutbondCallRequest(request: TeleConsultation.OutbondCalling.Request, method: HTTPMethod)
}

protocol TeleConsultationDataStore {
    //var name: String { get set }
}

class TeleConsultationInteractor: TeleConsultationBusinessLogic, TeleConsultationDataStore {
    var presenter: TeleConsultationPresentationLogic?
    var worker: TeleConsultationWorker?
    //var name: String = ""

    // MARK: Do something

    func doPostGetConsultationList(request: TeleConsultation.GetConsultationRecords.PendingRecordRequest, method: HTTPMethod) {
        worker = TeleConsultationWorker()
        worker?.presenter = self.presenter
        worker?.postRequestForConsultationList(request: request, method: method)
    }

    func doPostGetCompletedList(request: TeleConsultation.GetConsultationRecords.CompletedRecordRequest, method: HTTPMethod) {
        worker = TeleConsultationWorker()
        worker?.presenter = self.presenter
        worker?.postRequestForCompletedList(request: request, method: method)
    }

    func getStatusList() {
        worker = TeleConsultationWorker()
        worker?.presenter = self.presenter
        worker?.getRequestForStatusList()
    }

    func doPostFeedbackRequest(request: TeleConsultation.SubmitFeedback.Request, method: HTTPMethod) {
        worker = TeleConsultationWorker()
        worker?.presenter = self.presenter
        worker?.postFeedbackRequest(request: request, method: method)
    }
    
    func doOutbondCallRequest(request: TeleConsultation.OutbondCalling.Request, method: HTTPMethod) {
        worker = TeleConsultationWorker()
        worker?.presenter = self.presenter
        worker?.postOutbondCallRequest(request: request, method: method)
    }
}
