import UIKit

enum ConfirmCustomerOTP {
    // MARK: Use cases

    enum ResendOTP {

        struct BelitaRequest: Codable {
            let appointment_id: String
            let customer_id: String
            let type: String
            let is_custom: String
        }

        struct PaymentRequest: Codable {
            let appointment_id: String
            let customer_id: String
            let type: String
            let is_custom: String
            let quote_id: String
        }

        struct Response: Codable {
            var otp: String
            var status: Bool = false
            var message: String = ""
        }
    }

    enum ConfirmOTP {

        struct Request: Codable {
            let appointment_id: String
            let otp: String
            let type: String
            let is_custom: String
        }

        struct Response: Codable {
            var verified: Bool = false
            var status: Bool = false
            var message: String = ""
        }
    }
}
