import UIKit

protocol InvoicePresentationLogic {
    func presentGetInvoiceDetailsSuccess<T: Decodable>(response: T)
    func presentUpdateAppointmentStatus<T: Decodable>(response: T)
    func presentGetMyWalletRewardPointAndPackagesSuccess<T: Decodable>(response: T)
    func presentError(responseError: String?)
}

class InvoicePresenter: InvoicePresentationLogic {
  weak var viewController: InvoiceDisplayLogic?

  // MARK: Do something

    func presentGetInvoiceDetailsSuccess<T>(response: T) where T: Decodable {
        viewController?.displayInvoiceDetailsSuccess(viewModel: response)
    }

    func presentError(responseError: String?) {
        viewController?.displayError(errorMessage: responseError)
    }

    func presentUpdateAppointmentStatus<T>(response: T) where T: Decodable {
        viewController?.displayAppointmentSuccess(viewModel: response)
    }

    func presentGetMyWalletRewardPointAndPackagesSuccess<T: Decodable>(response: T) {
        viewController?.displayCustomerBalanceSuccess(viewModel: response)
    }
}
