import UIKit

protocol BillOfMaterialPresentationLogic {
    func presentGetBOMRelatedProductsSuccess<T: Decodable>(response: T)
    func presentError(responseError: String?)
}

class BillOfMaterialPresenter: BillOfMaterialPresentationLogic {
  weak var viewController: BillOfMaterialDisplayLogic?

  // MARK: Do something

    func presentGetBOMRelatedProductsSuccess<T>(response: T) where T: Decodable {
