import UIKit

class ApplyCouponWorker {
    let networkLayer = NetworkLayerAlamofire()
    var presenter: ApplyCouponPresentationLogic?

    func doApplyCoupon(request: PaymentMode.ApplyCoupon.Request) {

        let errorHandler: (String) -> Void = { (error) in
            self.presenter?.presentError(responseError: error)
        }
        let successHandler: (PaymentMode.ApplyCoupon.Response) -> Void = { (response) in
            self.presenter?.presentSuccess(response: response)
        }

        self.networkLayer.post(urlString: ConstantAPINames.applyCoupon.rawValue, body: request,
                               headers: ["Authorization": "Bearer \(GenericClass.sharedInstance.isuserLoggedIn().accessToken)"],
                               successHandler: successHandler, errorHandler: errorHandler, method: .post)
    }

    func getRequestWithParameter(request: ApplyCouponModel.GetCoupons.Request) {

        // Error handling redirection
        let errorHandler: (String) -> Void = { (error) in
            print(error)
            self.presenter?.presentError(responseError: error)
        }

        // Success handling redirection
        let successHandler: (ApplyCouponModel.GetCoupons.Response) -> Void = { (employees) in

            //self.view?.displayEmployees(employees: employees)
            self.presenter?.presentSuccess(response: employees)
        }

        // URL <baseurl>/products/?<appendFilters>
        var strURL: String = ConstantAPINames.couponsAPI.rawValue
        strURL += request.queryString

        // GET : API call
        self.networkLayer.get(urlString: strURL, successHandler: successHandler, errorHandler: errorHandler)
    }
}
