import UIKit

protocol ModifyAppointmentBusinessLogic {
    func doGetServiceListData(salon_id: String, service_type: String)
    func doPostDeleteServiceRequest(request: ModifyAppointment.DeleteAppointment.Request, serviceId: String)
    func doGetAppointmentList(request: Schedule.GetAppointnents.AppointmentDetailsRequest, method: HTTPMethod)
    func doPostUpdateAppointmentStatus(appointmentId: String, request: JobCard.ChangeAppointmentStatus.Request)
}

class ModifyAppointmentInteractor: ModifyAppointmentBusinessLogic {
    var presenter: ModifyAppointmentPresentationLogic?
    var worker: ModifyAppointmentWorker?
    //var name: String = ""

    // MARK: Do something

    func doGetServiceListData(salon_id: String, service_type: String) {
        worker = ModifyAppointmentWorker()
        worker?.presenter = self.presenter
        worker?.getRequestForServiceList(salon_id: salon_id, service_type: service_type)
    }

    func doPostDeleteServiceRequest(request: ModifyAppointment.DeleteAppointment.Request, serviceId: String) {
        worker = ModifyAppointmentWorker()
        worker?.presenter = self.presenter
        worker?.deleteRequestForService(request: request, serviceId: serviceId)
    }

    func doGetAppointmentList(request: Schedule.GetAppointnents.AppointmentDetailsRequest, method: HTTPMethod) {
        worker = ModifyAppointmentWorker()
        worker?.presenter = self.presenter
        worker?.postRequestForAppointments(request: request, method: method)
    }

    func doPostUpdateAppointmentStatus(appointmentId: String, request: JobCard.ChangeAppointmentStatus.Request) {
        worker = ModifyAppointmentWorker()
        worker?.presenter = self.presenter
        worker?.postRequestForChangeAppointmentStatus(appointmentId: appointmentId, request: request)
    }
}
